func twoSum(nums []int, target int) []int {
	res := []int{}
	m := make(map[int] int)
	for i,value:=range nums{
		if j,exist := m[target-value];exist {
			res = append(res,j)
			res = append(res,i)
			return res
		}
		m[value] = i
	}
	return res
}
