```java
package com.waibizi.blog.framework.config;

import com.fasterxml.jackson.annotation.JsonAutoDetect;
import com.fasterxml.jackson.annotation.PropertyAccessor;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.jsontype.impl.LaissezFaireSubTypeValidator;
import org.springframework.boot.autoconfigure.condition.ConditionalOnMissingBean;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.data.redis.connection.RedisConnectionFactory;
import org.springframework.data.redis.core.RedisTemplate;
import org.springframework.data.redis.core.StringRedisTemplate;
import org.springframework.data.redis.serializer.Jackson2JsonRedisSerializer;

import java.net.UnknownHostException;

/**
 * @Author 歪鼻子
 * @Date 2020/8/4 0:11
 * @Description:
 * @Version 1.0
 */
@Configuration
public class RedisConfig {
    /**
     * 注解@ConditionalOnMissingBean是根据条件注入Bean，如果IOC容器当中存在这个name值的Bean，我们就不注入
     * @param redisConnectionFactory
     * @return
     */
    @Bean
    @ConditionalOnMissingBean(name = "redisTemplate")
    public RedisTemplate<String,Object> redisTemplate(RedisConnectionFactory redisConnectionFactory){
        /* 序列化比较参考:https://www.cnblogs.com/donfaquir/p/10594199.html */
        Jackson2JsonRedisSerializer<Object> jackson2JsonRedisSerializer =
                new Jackson2JsonRedisSerializer<>(Object.class);
        /* ObjectMapper可以将Java对象匹配JSON结构 */
        ObjectMapper objectMapper = new ObjectMapper();
        /* 设置属性的自动发现 */
        objectMapper.setVisibility(PropertyAccessor.ALL, JsonAutoDetect.Visibility.ANY);
        objectMapper.activateDefaultTyping(LaissezFaireSubTypeValidator.instance,ObjectMapper.DefaultTyping.NON_FINAL);
        jackson2JsonRedisSerializer.setObjectMapper(objectMapper);
        RedisTemplate<String, Object> redisTemplate = new RedisTemplate<>();
        /* 对于Redis的一些参数设置 */
        redisTemplate.setConnectionFactory(redisConnectionFactory);
        redisTemplate.setKeySerializer(jackson2JsonRedisSerializer);
        redisTemplate.setValueSerializer(jackson2JsonRedisSerializer);
        redisTemplate.setHashKeySerializer(jackson2JsonRedisSerializer);
        redisTemplate.setHashValueSerializer(jackson2JsonRedisSerializer);
        /* 初始化bean的时候执行，可以针对某个具体的bean进行配置 */
        redisTemplate.afterPropertiesSet();
        return redisTemplate;
    }

    /**
     * 对于字符串的Redis操作
     * @param redisConnectionFactory
     * @return
     * @throws UnknownHostException
     */
    @Bean
    @ConditionalOnMissingBean(StringRedisTemplate.class)
    public StringRedisTemplate stringRedisTemplate(RedisConnectionFactory redisConnectionFactory) {
        StringRedisTemplate stringRedisTemplate = new StringRedisTemplate();
        stringRedisTemplate.setConnectionFactory(redisConnectionFactory);
        return stringRedisTemplate;
    }
}
```



### 将Redis的位图操作分离出来

```java
package com.waibizi.blog.framework.common.utils;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.data.redis.core.RedisCallback;
import org.springframework.data.redis.core.RedisTemplate;
import org.springframework.stereotype.Component;

/**
 * @Author 歪鼻子
 * @Date 2020/8/4 1:29
 * @Description:
 * @Version 1.0
 */
@Component
public class RedisUtils {
    @Autowired
    RedisTemplate redisTemplate;

    /**
     * 位图Set操作
     * @param key
     * @param offset 参数必须大于或等于 0 ，小于 2^32 (bit 映射被限制在 512 MB 之内)
     * @param value
     */
    public void setBit(String key, Long offset, Boolean value) {
        redisTemplate.execute((RedisCallback<Boolean>)
                con ->con.setBit(key.getBytes(),offset,value));
    }

    public Boolean getBit(String key, Integer offset){
        return (Boolean) redisTemplate.execute((RedisCallback<Boolean>)
                con -> con.getBit(key.getBytes(),offset));
    }

    /**
     * 计算位图的key的值是多少，即统计
     * @param key
     * @return
     */
    public Long bitCount(String key) {
        return (Long) redisTemplate.execute((RedisCallback<Long>)
                con -> con.bitCount(key.getBytes()));
    }

}
```



