public class NIOClient1 {
    public static void main(String[] args) throws IOException {
        //得到一个网络通道
        SocketChannel channel = SocketChannel.open();
        //设置为非阻塞
        channel.configureBlocking(false);
        //提供服务器端的ip和端口
        InetSocketAddress inetSocketAddress = new InetSocketAddress("127.0.0.1",6666);
        //连接服务器
        if (!channel.connect(inetSocketAddress)){
            while (!channel.finishConnect()){
                System.out.println("客户端可以边连接边进行其他的操作");
            }
        }
        //如果连接成功，就开始发送数据
        String msg = "歪鼻子已就位!";
        //将发送数据以字节的形式写入到buffer当中
        ByteBuffer buffer = ByteBuffer.wrap(msg.getBytes());
        //将buffer写入到channel当中
        channel.write(buffer);
        System.in.read();
    }
}
package com.waibizi.group;


import java.io.IOException;
import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.SocketChannel;
import java.util.Iterator;
import java.util.Scanner;

/**
 * @Author 歪鼻子
 * @Date 2020/7/9 16:34
 * @Description:
 * @Version 1.0
 */
public class ClientA {
    /* 服务的IP地址 */
    private final String HOST = "127.0.0.1";
    /* 服务的端口 */
    private final int PORT = 2048;
    private Selector selector;
    private SocketChannel socketChannel;
    private String name;

    public ClientA() throws IOException {
        /* 打开选择器 */
        selector = Selector.open();
        /* 连接服务器 */
        socketChannel = SocketChannel.open(new InetSocketAddress(HOST,PORT));
        /* 设置为非阻塞状态 */
        socketChannel.configureBlocking(false);
        /* 将通道注册到选择器上 */
        socketChannel.register(selector, SelectionKey.OP_READ);
        name = "ClientA";
        System.out.println(name+" is ok····");
    }
    public void send(String info){
        info = name + "说："+info;
        try {
            socketChannel.write(ByteBuffer.wrap(info.getBytes()));
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
    public void read() throws IOException {
        while (selector.select()>0){
            Iterator<SelectionKey> iterator = selector.selectedKeys().iterator();
            while (iterator.hasNext()){
                SelectionKey key = iterator.next();
                if (key.isReadable()){
                    /* 得到相关的通道 */
                    SocketChannel channel = (SocketChannel) key.channel();
                    /* 得到一个buffer */
                    ByteBuffer buffer = ByteBuffer.allocate(1024);
                    /* 读取 */
                    channel.read(buffer);
                    System.out.println(new String(buffer.array()).trim());
                }
            }
            iterator.remove();
        }
    }

    public static void main(String[] args) throws IOException {
        /* 启动客户端 */
        ClientA client = new ClientA();
        new Thread(()->{
            while (true){
                try {
                    client.read();
                    Thread.currentThread().sleep(3000L);
                } catch (IOException | InterruptedException e) {
                    e.printStackTrace();
                }
            }
        }).start();
        /* 向客户端发送数据 */
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNextLine()){
            String msg = scanner.nextLine();
            client.send(msg);
        }
    }
}
